import React, { useState } from 'react'
import { connect } from 'react-redux'
import { getProjects, postProject, editProject, removeProject } from '../store/actions/projectActions'
import '../styles/allProjects.css'
import ProjectForm from '../components/ProjectForm'
import ProjectItem from '../components/ProjectItem'

function AllProjects(props) {
    const { projects, errors, postProject, history, editProject, removeProject } = props
    const [edit, setEdit] = useState(false)
    const [projectId, setProjectId] = useState("")
    console.log(projectId)
    let projectsMap = projects.map((project, i) =>
        <ProjectItem
            key={i}
            setProjectId={setProjectId}
            setEdit={setEdit}
            index={i + 1}
            history={history}
            project={project}
        />)

    return (
        <div className="allProjectsContainer">
            <h1 className="display-6">Projects</h1>
            <button onClick={() => setEdit(false)} type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#projectModal">
                Create
            </button>
            <div className="mappedProjects">
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Title</th>
                            <th scope="col">Start Date</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        {projectsMap}
                    </tbody>
                </table>
            </div>
            <div className="projectForm">
                <ProjectForm
                    edit={edit}
                    postProject={postProject}
                    editProject={editProject}
                    projectId={projectId}
                />
            </div>
            <div className="modal" id="removeModal" tabIndex="-1">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Confirm Delete</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <button onClick={() => removeProject(projectId)} type="button" className="btn btn-secondary" data-bs-dismiss="modal">Remove</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        projects: state.projects,
        errors: state.errors,
    }
}

export default connect(mapStateToProps, { getProjects, postProject, editProject, removeProject })(AllProjects)
