import { apiCall } from "../../services/api";

//api call syntax apiCall(method, path, payload)

export function getIssues() {
    return dispatch => {
        apiCall("get", "/api/issues")
        .then(res => {
            dispatch({ type: "LOAD_ISSUES", issues: res})
            console.log(res)
        })
    }
}

export function postIssue() {
    return apiCall("post", )
}
