import { ADD_ERROR, REMOVE_ERROR } from "../actionTypes"

export default (state = { message: null }, action) => {
    switch (action.type) {
        case ADD_ERROR:
            return { ...state, message: action.errorMessage}
        case REMOVE_ERROR:
            console.log("reducer")
            return { message: null }
        default:
            return state
    }
}
