import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import { postIssue } from '../store/actions/issueActions'
import IssuesForm from '../components/IssuesForm'
import Toggler from '../hooks/toggler'

function MyIssues({issues, postIssue}) {
    const [showForm, toggleShowForm] = Toggler(true)
    const mappedIssues = issues.map((issue, i) => {
        return <li key={i}>{issue.title}</li>
    })
    console.log(issues)
    return (
        <div>
            <ul>
                {mappedIssues}
            </ul>
            {showForm && 
                <div>
                    <IssuesForm postIssue={postIssue}/>
                </div>
            }
        </div>
    )
}

const mapStateToProps = state => {
    return {
        issues: state.issues,
        errors: state.errors,
    }
}

export default connect(mapStateToProps, { postIssue })(MyIssues)
