import { LOAD_ISSUES, REMOVE_ISSUES, UPDATE_ISSUES } from "../actionTypes"

export default (state = {}, action) => {
    switch(action.type) {
        case LOAD_ISSUES: 
            return [action.issues]
        case REMOVE_ISSUES: 
            return state.filter(issue => issue._id !== action.id )
        case UPDATE_ISSUES:
            return state.map(issue => (
                issue._id === action.id ? { ...issue, name: action.name } : issue
            )) 
        default:
            return state
    }
}
