import React, { useState } from 'react'
import { withRouter } from 'react-router-dom'

function ProjectForm({ postProject, history, edit, editProject, projectId }) {
    const [state, setstate] = useState({
        projectName: undefined,
        description: "whack",
        startDate: undefined,
        targetEndDate: undefined,
    })

    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(e.target.name)
        if (e.target.name === "create") {
            console.log("here")
            postProject(state)
        } else {
                    console.log("ok")
                    editProject(projectId, state)
        }
        // switch (e.target.name) {
        //     case "create":
        //         console.log("here")
        //         postProject(state)
        //         // history.go(0)
        //     case "edit":
        //         console.log("ok")
        //         editProject(projectId, state)
        //     default:
        //         return {}
        // }
    }

    const handleChange = (e) => {
        setstate({ ...state, [e.target.name]: e.target.value })
    }
    return (
        <div>
            <div className="modal fade" id="projectModal" tabIndex="-1">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Modal title</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleSubmit}>
                                <div className="loginInputs">
                                    <div className="mb-3" >
                                        <label htmlFor="projectName" className="form-label">Project Name</label>
                                        <input onChange={handleChange} type="text" className="form-control" name="projectName" />
                                    </div>
                                    <div className="mb-3" >
                                        <label htmlFor="startDate" className="form-label">Start Date</label>
                                        <input onChange={handleChange} type="date" className="form-control" name="startDate" />
                                    </div>
                                    <div className="mb-3" >
                                        <label htmlFor="targetEndDate" className="form-label">Target End Date</label>
                                        <input onChange={handleChange} type="date" className="form-control" name="targetEndDate" />
                                    </div>
                                </div>
                            </form>
                        </div>
                        {edit ?
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                {/* <button onClick={handleSubmit} name="edit" type="button" className="btn btn-primary">Edit changes</button> */}
                            </div>
                            :
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                <button onClick={handleSubmit} name="create" type="button" className="btn btn-primary">Create Project</button>
                            </div>
                        }
                    </div>
                </div>
            </div>
        </div>
    )

}

export default withRouter(ProjectForm)
