import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import { getProjects, postProject } from '../store/actions/projectActions'
import { Link } from 'react-router-dom'
import '../styles/allProjects.css'
import ProjectForm from '../components/ProjectForm'
import ProjectItem from '../components/ProjectItem'
import Toggler from '../hooks/toggler'

function AllProjects(props) {
    const { projects, errors, postProject } = props
    const [showForm, toggleShowForm] = Toggler(false)

    let projectsMap = projects.map((pro, i) =>
        <div key={i}>
            <Link to={`/projects/${pro._id}`}>{pro.projectName}</Link>
        </div>)

    return (
        <div className="allProjectsContainer">
            <div>
                <h1 className="display-6">All Projects</h1>
            </div>
            <div className="mappedProjects">

                {projectsMap}
            </div>
            {showForm &&
                <div className="projectForm">
                    <ProjectForm postProject={postProject} />

                </div>
            }
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        projects: state.projects,
        errors: state.errors,
    }
}

export default connect(mapStateToProps, { getProjects, postProject })(AllProjects)
