import React, { useState } from 'react'
import { connect } from 'react-redux'
import { postIssue, deleteIssue, editIssue } from '../store/actions/issueActions'
import IssuesForm from '../components/IssuesForm'
import IssueItem from '../components/IssueItem'
import '../styles/allIssues.css'
import { removeError } from '../store/actions/errorActions'
import Table from "react-bootstrap/table"

function MyIssues({ users, issues, postIssue, history, projects, deleteIssue, editIssue, errors, removeError }) {
    const [edit, setEdit] = useState(false)
    const [issueId, setIssueId] = useState("")
    const [show, setShow] = useState(false);
    const mappedIssues = issues.map((issue, i) => (
        <IssueItem
            issue={issue}
            key={i}
            index={i + 1}
            history={history}
            setEdit={setEdit}
            setIssueId={setIssueId}
            show={setShow}
        />
    ))

    const handleShow = () => setShow(true);

    history.listen(() => {
        removeError()
    })

    const issueDetails = issues.find(issue => issue._id === issueId)

    return (
        <div className="allIssuesContainer">
            <h1 className="display-6">Issues</h1>
            <button onClick={() => { setEdit(false); handleShow() }} type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#issueModal">
                Create Issue
            </button>
            <div className="mappedIssues">
                <Table hover>
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Title</th>
                            <th scope="col">Project Name</th>
                            <th scope="col">Developer</th>
                            <th scope="col">Priority</th>
                            <th scope="col">Status</th>
                            <th scope="col">Type</th>
                            <th scope="col">Created</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        {mappedIssues}
                    </tbody>
                </Table>
            </div>
            <div>
                <IssuesForm
                    postIssue={postIssue}
                    edit={edit}
                    projects={projects}
                    editIssue={editIssue}
                    issueId={issueId}
                    users={users}
                    errors={errors}
                    issue={issueDetails}
                    setIssueId={setIssueId}
                    show={show}
                    setShow={setShow}
                />
            </div>
            <div className="modal" id="removeIssuesModal" tabIndex="-1">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Confirm Issue Delete</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <button onClick={() => deleteIssue(issueId)} type="button" className="btn btn-secondary" data-bs-dismiss="modal">Remove</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        issues: state.issues,
        errors: state.errors,
        users: state.users,
    }
}

export default connect(mapStateToProps, { postIssue, deleteIssue, editIssue, removeError })(MyIssues)
