import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import { postIssue } from '../store/actions/issueActions'
import IssuesForm from '../components/IssuesForm'
import IssueItem from '../components/IssueItem'

function MyIssues({issues, postIssue}) {
    const mappedIssues = issues.map((issue, i) => {
        return <IssueItem key={i} />
    })

    return (
        <div>
            <div className="mappedIssues">
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Title</th>
                            <th scope="col">Start Date</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        {mappedIssues}
                    </tbody>
                </table>
            </div>
            {/* {showForm && 
                <div>
                    <IssuesForm postIssue={postIssue}/>
                </div>
            } */}
        </div>
    )
}

const mapStateToProps = state => {
    return {
        issues: state.issues,
        errors: state.errors,
    }
}

export default connect(mapStateToProps, { postIssue })(MyIssues)
