import axios from "axios";
import { apiCall } from "../../services/api";
import { SET_CURRENT_USER } from "../actionTypes";
import { addError, removeError } from "./errorActions";

function setUser(user) {
    return {
        type: SET_CURRENT_USER,
        user
    }
}

export function setTokenHeader(token) {
    if (token) { 
        console.log(token)
        axios.defaults.header.common['Authorization'] = `Bearer ${token}`
    } else {
        axios.defaults.header.common['Authorization'] = {}
    }
}

export function authUser(type, userData = {}) {
    return dispatch => {
        return new Promise((resolve, reject) => {
           apiCall('post', `api/auth/${type}`, userData)
           .then(({token, ...userInfo}) => {
                localStorage.setItem("jawt", token)
                // setTokenHeader(token)
                dispatch(setUser(userInfo))
                // dispatch(removeError())
                dispatch(addError("error"))
                resolve()
           })
           .catch(err => {
               dispatch(addError(err))
               reject() 
           }) 
        })
    }
}
