import React from 'react';
import './App.css';
import { Route, Switch } from 'react-router-dom';
import Main from '../components/Main'
import AuthForm from '../components/AuthForm'
import Test from '../components/Test'
import { connect } from 'react-redux';
import { authUser } from '../store/actions/userActions';

function App(props) {
  const { currentUser, errors, authUser } = props
  console.log(currentUser)
  return (
    <div>
      <Switch>
        <Route exact path="/" render={routeProps => <Main {...routeProps} currentUser={currentUser}/>} />
        <Route exact path="/login" 
          render={routeProps => 
            <AuthForm
              errors={errors}
              authUser={authUser}
              {...routeProps}
            />
          } 
        />
        <Route exact path="/signup" 
          render={routeProps => 
            <AuthForm
              authUser={authUser}
              errors={errors}
              signup
              {...routeProps}
            />
          } 
        />
        <Route exact path="/test" render={() => <Test />} />
      </Switch>
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    currentUser: state.currentUser,
    errors: state.errors
  }
}

export default connect(mapStateToProps, { authUser })(App);
