import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'

function AllProjects(props) {
    const { projects, currentUser } = props
    const [ myProjects, setMyProjects ] = useState()
    useEffect(() => {
        setMyProjects(projects.filter((project) => project.createdBy._id === currentUser.user._id))
    }, [])
    console.log(myProjects)
    let projectsMap = projects.map((pro, i) =>
        <div key={i}>
            <Link to={`/projects/${pro._id}`}>{pro.projectName}</Link>
        </div>)
    const handleChange = () => {

    }
    return (
        <div>
            <button onClick={handleChange}>CLick on me</button>
            {projectsMap}
        </div>
    )
}

export default AllProjects
